import React, { useState } from "react";
import Card from "./components/UI/Card";
import ExpenseItem from "./components/Expenses/ExpenseItem";
import NewExpense from "./components/NewExpense/NewExpense";
import ExpensesFilter from "./components/Filters/ExpensesFilter";

const DUMMY_EXPENSES = [
  {
    id: 'el1',
    title: 'Toilet paper',
    amount: 94.65,
    date: new Date(2021, 7, 14)
  },
  {
    id: 'el2',
    title: 'Car Insurance',
    amount: 284.98,
    date: new Date(2022, 2, 14)
  },
  {
    id: 'el3',
    title: 'New Tv',
    amount: 494.65,
    date: new Date(2022, 3, 24)
  },
  {
    id: 'el4',
    title: 'food',
    amount: 194.65,
    date: new Date(2022, 4, 16)
  }
];

const App = () => {
  const [filteredYear, setFilteredYear] = useState('2020');
  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);

  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  }

  const choosedYear = (year) => {
    setFilteredYear(year);
    const filteredItems = expenses.filter(expense => expense.date.getFullYear() == year);
    console.log(year);
    console.log(filteredItems);

  }

  return (

      <Card className="expenses">
        <NewExpense onAddExpense={addExpenseHandler}/>
        < ExpensesFilter selected={filteredYear} onYearChange={choosedYear}/>
        {expenses.map(expense => 
            <ExpenseItem  
            key= {expense.id}
            title={expense.title} 
            amount={expense.amount} 
            date={expense.date} />
          )
        }
     </Card>
  );

}

export default App;
